mapper.select:

SELECT *
FROM TEST01_MEMBER
WHERE ID=#{id} and PWD=#{pwd}

SELECT *
FROM TEST01_MEMBER
WHERE ID=#{id} AND PHONE=#{phone}

SELECT PHONE
FROM TEST01_MEMBER
WHERE PHONE=#{phone}

mapper.update:
UPDATE TEST01_MEMBER SET
PWD=#{pwd}, ADDRESS1=#{address1}, ADDRESS2=#{address2}, PHONE=#{phone}
WHERE ID=#{id}
