mapper.select:
SELECT FOOD_SEQ, ID, TITLE, TOTAL_CNT, JOIN_CNT, MEETINGDAY, CONTENT, WDATE, READ_CNT, LIKE_CNT, LOCAL, DEL, (SELECT COUNT(*) FROM TEST01_REPLY WHERE FOOD_SEQ=A.FOOD_SEQ) AS REPLY_CNT FROM (SELECT ROW_NUMBER() OVER(ORDER BY FOOD_SEQ DESC)AS RNUM, FOOD_SEQ, ID, TITLE, TOTAL_CNT, JOIN_CNT, MEETINGDAY, CONTENT, WDATE, READ_CNT, LIKE_CNT, LOCAL, DEL FROM TEST01_FOOD WHERE 1=1
AND TITLE LIKE '%'||#{f_keyword}||'%'
AND CONTENT LIKE '%'||#{f_keyword}||'%'
AND ID LIKE '%'||#{f_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

mapper.update:
UPDATE TEST01_FOOD
SET TITLE=#{title}, TOTAL_CNT=#{total_cnt}, MEETINGDAY=#{meetingday}, CONTENT=#{content}, LOCAL=#{local}
WHERE FOOD_SEQ=#{food_seq}

UPDATE TEST01_FOOD
SET DEL=1
WHERE FOOD_SEQ=#{food_seq}

mapper.select:
SELECT FULLNAME, FOOD_SEQ, RDATE
FROM TEST01_ATTACH 
WHERE FOOD_SEQ=#{food_seq}

mapper.update:
UPDATE TEST01_FOOD
SET READ_CNT = READ_CNT + 1
WHERE FOOD_SEQ=#{food_seq}

mapper.select:
SELECT LIKE_SEQ,FOOD_SEQ, ID
FROM TEST01_LIKE
WHERE FOOD_SEQ = #{food_seq} AND ID=#{id}

SELECT JOIN_SEQ, ID, FOOD_SEQ
FROM TEST01_JOIN
WHERE FOOD_SEQ = #{food_seq} AND ID=#{id}
