mapper.select:
SELECT ID
FROM MEMBER
WHERE ID=#{id}

SELECT EMAIL
FROM MEMBER
WHERE EMAIL=#{email}

SELECT PHONE
FROM MEMBER
WHERE PHONE=#{phone}

mapper.update:
UPDATE MEMBER SET
AUTHKEY=#{authkey}
WHERE ID=#{id}

UPDATE MEMBER SET
AUTHSTATUS=1
WHERE EMAIL=#{email}

  	UPDATE MEMBER SET
	DEL=1
	WHERE ID=#{id}
